import (
	context "context"
	fern "github.com/client-side-params/fern"
	fernclient "github.com/client-side-params/fern/client"
)

client := fernclient.NewClient()
response, err := client.Service.ListResources(
	context.TODO(),
	&fern.ListResourcesRequest{
		Page:          1,
		PerPage:       1,
		Sort:          "created_at",
		Order:         "desc",
		IncludeTotals: true,
		Fields: fern.String(
			"fields",
		),
		Search: fern.String(
			"search",
		),
	},
)

