import (
	context "context"
	fern "github.com/nullable-request-body/fern"
	fernclient "github.com/nullable-request-body/fern/client"
)

client := fernclient.NewClient()
response, err := client.TestGroup.TestMethodName(
	context.TODO(),
	&fern.TestMethodNameTestGroupRequest{
		PathParam: "path_param",
		Body:      &fern.PlainObject{},
	},
)
