import (
	context "context"
	fern "github.com/oauth-client-credentials-default/fern"
	fernclient "github.com/oauth-client-credentials-default/fern/client"
)

client := fernclient.NewClient()
response, err := client.Auth.GetToken(
	context.TODO(),
	&fern.GetTokenRequest{
		ClientId:     "string",
		ClientSecret: "string",
	},
)
