import (
	context "context"
	fern "github.com/oauth-client-credentials-mandatory-auth/fern"
	fernclient "github.com/oauth-client-credentials-mandatory-auth/fern/client"
	option "github.com/oauth-client-credentials-mandatory-auth/fern/option"
)

client := fernclient.NewClient(
	option.WithClientCredentials(
		"<YOUR_CLIENT_ID>",
		"<YOUR_CLIENT_SECRET>",
	),
)
