import (
	context "context"
	fern "github.com/optional/fern"
	fernclient "github.com/optional/fern/client"
)

client := fernclient.NewClient()
response, err := client.Optional.SendOptionalNullableWithAllOptionalProperties(
	context.TODO(),
	"actionId",
	"id",
	&fern.DeployParams{
		UpdateDraft: fern.Bool(
			true,
		),
	},
)

import (
	context "context"
	fernclient "github.com/optional/fern/client"
)

client := fernclient.NewClient()
response, err := client.Optional.SendOptionalBody(
	context.TODO(),
	map[string]interface{}{
		"string": map[string]interface{}{
			"key": "value",
		},
	},
)

import (
	context "context"
	fern "github.com/optional/fern"
	fernclient "github.com/optional/fern/client"
)

client := fernclient.NewClient()
response, err := client.Optional.SendOptionalTypedBody(
	context.TODO(),
	&fern.SendOptionalBodyRequest{
		Message: "message",
	},
)
