import (
	context "context"
	fern "github.com/simple-api/fern"
	fernclient "github.com/simple-api/fern/client"
	option "github.com/simple-api/fern/option"
)

client := fernclient.NewClient(
	option.WithToken(
		"<YOUR_AUTH_TOKEN>",
	),
	option.WithBaseURL(
		fern.Environments.Production,
	),
)
response, err := client.User.Get(
	context.TODO(),
	"id",
)
