import (
	context "context"
	fern "github.com/websocket-inferred-auth/fern"
	fernclient "github.com/websocket-inferred-auth/fern/client"
)

client := fernclient.NewClient()
response, err := client.Auth.GetTokenWithClientCredentials(
	context.TODO(),
	&fern.GetTokenRequest{
		XApiKey:      "X-Api-Key",
		ClientId:     "client_id",
		ClientSecret: "client_secret",
	},
)

import (
	context "context"
	fern "github.com/websocket-inferred-auth/fern"
	fernclient "github.com/websocket-inferred-auth/fern/client"
)

client := fernclient.NewClient()
response, err := client.Auth.RefreshToken(
	context.TODO(),
	&fern.RefreshTokenRequest{
		XApiKey:      "X-Api-Key",
		ClientId:     "client_id",
		ClientSecret: "client_secret",
		RefreshToken: "refresh_token",
	},
)
