import { SeedAnyAuthClient } from "../../src/Client";
import { mockServerPool } from "../mock-server/MockServerPool";
import { mockAuth } from "./mockAuth";

describe("Auth", () => {
    test("getToken", async () => {
        const server = mockServerPool.createServer();
        mockAuth(server);

        const client = new SeedAnyAuthClient({
            token: "test",
            apiKey: "test",
            clientId: "client_id",
            clientSecret: "client_secret",
            environment: server.baseUrl,
        });
        await await client.auth.getToken({
            client_id: "client_id",
            client_secret: "client_secret",
            scope: "scope",
        });

        const rawRequestBody = {
            client_id: "client_id",
            client_secret: "client_secret",
            audience: "https://api.example.com",
            grant_type: "client_credentials",
            scope: "scope",
        };
        const rawResponseBody = { access_token: "access_token", expires_in: 1, refresh_token: "refresh_token" };
        server
            .mockEndpoint()
            .post("/token")
            .jsonBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.auth.getToken({
            client_id: "client_id",
            client_secret: "client_secret",
            scope: "scope",
        });
        expect(response).toEqual({
            access_token: "access_token",
            expires_in: 1,
            refresh_token: "refresh_token",
        });
    });
});
