    test("getWithBearer", async () => {
        const server = mockServerPool.createServer();
        mockOAuth(server);

        const client = new SeedEndpointSecurityAuthClient({
            maxRetries: 0,
            token: "test",
            apiKey: "test",
            clientId: "client_id",
            clientSecret: "client_secret",
            environment: server.baseUrl,
        });

        const rawResponseBody = [
            { id: "id", name: "name" },
            { id: "id", name: "name" },
        ];
        server.mockEndpoint().get("/users").respondWith().statusCode(200).jsonBody(rawResponseBody).build();

        const response = await client.user.getWithBearer();
        expect(response).toEqual([
            {
                id: "id",
                name: "name",
            },
            {
                id: "id",
                name: "name",
            },
        ]);
    });

