import { mockServerPool } from "../mock-server/MockServerPool";
import { SeedApiClient } from "../../src/Client";

describe("SeedApiClient", () => {
    test("uploadJsonDocument", async () => {
        const server = mockServerPool.createServer();
        const client = new SeedApiClient({ token: "test", environment: server.baseUrl });
        const rawRequestBody = {};
        const rawResponseBody = { author: "author", id: 1, tags: [{ key: "value" }], title: "title" };
        server
            .mockEndpoint()
            .post("/documents/upload")
            .jsonBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.uploadJsonDocument();
        expect(response).toEqual({
            author: "author",
            id: 1,
            tags: [
                {
                    key: "value",
                },
            ],
            title: "title",
        });
    });
});
